mapper.select:
SELECT

FROM mq_user
WHERE
1 = 1
AND open_id = #{openId}

    AND phone = #{phone}

mapper.update:
UPDATE mq_user SET is_vip = 0
,parent = #{parent}
WHERE id = ( SELECT o.user_id FROM mq_order o RIGHT JOIN mq_commodity c ON o.commodity_id = c.id WHERE o.order_number = #{orderNumber} and c.commodity_state = #{commodityState} LIMIT 1 )

UPDATE mq_user
 SET
 login = #{login}
 WHERE id = #{suerId}
