mapper.select:
SELECT
DISTINCT(order_number)
FROM mq_order
WHERE
del_flag = '0'
AND user_id = #{userId}

    AND order_state = #{orderState}

ORDER BY order_number DESC
LIMIT #{pageNo},#{pageSize}

SELECT
FROM mq_order WHERE del_flag = '0' AND order_number in (
#{itme}
) ORDER BY order_number DESC

mapper.update:
UPDATE  mq_order
SET
order_state = #{orderState}
WHERE
id = #{id}

UPDATE mq_order
SET
order_state = #{orderState},
update_date = #{updateDate}
WHERE
order_number = #{orderNumber}

UPDATE mq_order
SET
order_state = #{orderState}
WHERE
id = #{id}
